INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (3, 'Litwa skontroluje spółkę kolejową', 'Będzie kontrola w spółce litewskiej, która blokowała Orlen Lietuva. Poprzedni prezes spółki odszedł w atmosferze skandalu. Problemem była wysokość taryf transportowych. spółka blokowała podpisanie porozumienia. Prezes kolei Stasys Dailydka odszedł w niesławie. Z powodu podwyższonych taryf za transport kolejowy, które obniżają rentowność pracy rafinerii w Możejkach.e', 3, NULL, NULL, 'http://biznesalert.pl/litwa-skontroluje-spolke-kolejowa-szansa-przelom-dla-orlenu/');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (5, 'Mała ale silna UE', 'François Fillon proponuje Merkel budowę małej, ale silnej Unii zdolnej postawić się Trumpowi. Berlin jest jednak sceptyczny. Zdaniem Fillona konieczne jest powołanie dla państw Eurolandu osobnego sekretariatu oraz harmonizacja podatków, w szczególności od zysku przedsiębiorstw. Aby przekształcić euro w równoważną do dolara walutę rezerwową, Fillon chce także utworzenia Europejskiego Funduszu Walutowego. Reakcja Niemiec jest twarda. – Nie zgodzimy się na nowe podziały w Europie, na sprowadzenie Polski do członka Unii drugiej kategorii.', 3, NULL, NULL, 'http://www.rp.pl/Polityka/301239878-Fillon-proponuje-Merkel-budowe-malej-silnej-Unii.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (6, 'Wlk. Brytania. Przygotowanie Ustawy w sp. brexitu', 'Wysoki Trybunał (High Court) orzekł, że do rozpoczęcia formalnego procesu wyjścia kraju z Unii Europejskiej niezbędna jest zgoda obu izb parlamentu, ale nie ma konieczności konsultacji z władzami lokalnymi.', 3, '2017-01-24 01:00:00+01', NULL, 'http://www.rp.pl/Brexit/170129491-Wielka-Brytania-Rzad-przygotowuje-ustawe-ws-Brexitu.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (7, 'Trump podpisuje zgodę na budowę rurociągu Keystone XL', 'Prezydent Donald Trump podjął na dalsze kroki w budowie dwóch rurociągów Keystone XL. Budowa wywołała wcześniej niezadowolenie wśród społeczności Indian Lakota. Obama zrezygnował z dalszej budowy rurociągu.', 3, '2017-01-24 01:00:00+01', NULL, 'http://www.cnbc.com/2017/01/24/trump-to-advance-keystone-dakota-pipelines-with-executive-order-on-tuesday-nbc.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (8, 'Ministerstwo Finansów pracuje nad split payment w VAT.', 'Stosowanie podzielonej płatności rozliczania VAT będzie dobrowolne, a przedsiębiorcy dostaną cały system zachęt, by z niej korzystać. Jednak w wybranych branżach split payment może być obowiązkowy.  Split payment to metoda rewolucyjna, rzadko stosowana w Unii Europejskiej. Polega na tym, że kupujący zamiast płacić pełną cenę brutto wpisaną na fakturze będzie dzielił płatność za kupiony towar (lub usługę) na dwie części. Pierwszą – kwotę netto wpłaci na rachunek bankowy sprzedającego. Drugą na konto VAT sprzedającego, do którego dostęp będzie miał też urząd skarbowy.', 1, '2017-01-26 01:00:00+01', NULL, 'http://gospodarka.dziennik.pl/news/artykuly/541063,ministerstwo-finansow-split-payment-w-vat-szczegoly-wiceminister-pawel-gruza-rozmowa.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (9, 'Zmiany w ordynacji w samorządach.', 'Jarosław Kaczyński pytany o cel zmian w ordynacji wyborczej do samorządu, m.in. wprowadzenie kadencyjności wójtów. "Ten cel jest prosty: zupełnie zmienić sytuację w samorządach w ten sposób, żeby Prawo i Sprawiedliwość, które dzisiaj ma stosunkowo niewielkie pozycje w tej sferze życia publicznego, te pozycje radykalnie wzmocniło. Dzisiaj nasza partia to jest taka duża głowa - to jest nasz klub parlamentarny, rząd i Senat - i dużo mniejszy tułów. PSL wygląda zupełnie inaczej: głowa mała, tułów wielki jak u dinozaura.".', 1, '2017-01-23 01:00:00+01', NULL, 'https://www.pb.pl/kaczynski-w-polsce-jest-mnostwo-ksiestewek-musza-byc-zlikwidowane-852795');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (10, 'Spotkanie May z Trumpem', 'Brytyjska premier Theresa May potwierdziła w niedzielę, że w piątek spotka się w Waszyngtonie z prezydentem Stanów Zjednoczonych, Donaldem Trumpem. Tym samym szefowa brytyjskiego rządu będzie pierwszym przywódcą, którego Trump przyjmie po objęciu stanowiska. Jak dodała, w trakcie spotkania planuje rozmawiać z Trumpem m.in. o \"specjalnej relacji\" pomiędzy Stanami Zjednoczonymi a Wielką Brytanią, przyszłością wolnego handlu, walce z terroryzmem, rozwiązaniem konfliktu w Syrii i przyszłości NATO.', 3, '2017-01-22 01:00:00+01', NULL, 'https://www.pb.pl/w-piatek-spotkanie-may-z-trumpem-852761');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (11, 'Bułgaria ma nowego prezydenta', 'Rumen Radew, 53-letni generał rezerwy objął w niedzielę funkcję głowy państwa, którą sprawować będzie w Bułgarii przez pięć lat.', 3, '2017-01-22 01:00:00+01', NULL, 'https://www.pb.pl/bulgaria-ma-nowego-prezydenta-852777');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (12, 'Miedwiediew: nie należy mieć złudzeń co do szybkiego zniesienia sankcji', 'Premier Rosji Dmitrij Miedwiediew powiedział w niedzielę, że nie należy się łudzić i liczyć na szybkie zniesienie nałożonych na jego kraj sankcji. Zdaniem premiera sankcje utrzymają się jeszcze długo.', 3, '2017-01-22 01:00:00+01', NULL, 'https://www.pb.pl/miedwiediew-nie-nalezy-miec-zludzen-co-do-szybkiego-zniesienia-sankcji-852793');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (13, 'Trump spotka się z prezydentem Meksyku', 'Do spotkania prezydenta Donalda Trumpa z prezydentem Meksyku Enriquem Peną Nieto dojdzie 31 stycznia w Waszyngtonie – podał w sobotę rzecznik Białego Domu Sean Spicer. Rozmowy na najwyższym szczeblu poprzedzi spotkanie delegacji rządowych w stolicy USA. Odbędzie się ono w dniach 25-26 stycznia – zaznaczył Spicer. Wśród tematów rozmów wymienia się kwestie związane z handlem dwustronnym i migracją oraz sprawy bezpieczeństwa. Obaj przywódcy zajmą się też zapewne uregulowaniem spraw związanych z umową o północnoamerykańskiej strefie wolnego handlu (NAFTA) zawartą w 1992 r. pomiędzy Stanami Zjednoczonymi, Kanadą i Meksykiem.', 3, '2017-01-22 01:00:00+01', NULL, 'https://www.pb.pl/trump-spotka-sie-z-prezydentem-meksyku-852758');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (14, 'Prezydent Meksyku odrzucił decyzję Trumpa by zapłacić za mur pomiędzy USA a Meksykiem', 'Prezydent Enrique Peña Nieto odrzucił decyzję by wybudować mur na granicy z USA i powtórzył, że jego kraj nie będzie za to płacił.', 3, '2017-01-26 01:00:00+01', NULL, 'https://www.theguardian.com/world/video/2017/jan/26/mexican-president-demands-respect-and-rejects-us-border-wall-video');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (17, '20% cła na import z Meksyku', 'Relations between the US and Mexico appeared to be heading for crisis on Thursday after Enrique Peña Nieto cancelled a meeting with Donald Trump and the White House retaliated by suggesting a new 20% tax on imports from its southern neighbour to finance the construction of a border wall.', 3, '2017-01-26 01:00:00+01', NULL, 'https://www.theguardian.com/world/2017/jan/26/trump-calls-for-20-tax-on-mexican-imports-to-pay-for-border-wall');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (18, 'UK prime minister meets GOP in Philly', 'Theresa May said the UK and the US "have a joint responsibility to lead", but the "days of Britain and America intervening in sovereign countries in an attempt to remake the world in our own image are over".', 3, '2017-01-26 01:00:00+01', NULL, 'https://www.theguardian.com/politics/2017/jan/26/opposites-attract-theresa-may-signals-strong-relationship-with-trump');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (19, 'Kongresmenka spotka się z Assadem', 'Representative Tulsi Gabbard of Hawaii, who met Trump after his election, went on a “fact-finding” mission to Syria and met President Bashar al-Assad, in the first meeting between a US elected official and Assad known to have occurred in years.', 3, '2017-01-26 01:00:00+01', NULL, 'https://www.theguardian.com/us-news/2017/jan/26/tulsi-gabbard-bashar-al-assad-syria-democrats');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (20, 'Kaczyński uderza w samorządy', 'Nadmierna stabilizacja władzy na poziomie samorządowym czasem służy rzeczom dobrym, ale służy także rzeczom złym, powstawaniu patologicznych powiązań, klik - mówił prezes PiS Jarosław Kaczyński tłumacząc konieczność zmian w ordynacji.', 1, '2017-01-21 01:00:00+01', NULL, 'https://www.pb.pl/kaczynski-uderza-w-samorzady-852755');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (21, 'Le Pen proklamuje powstanie "nowego świata"', 'Kandydatka na prezydenta Francji Marine Le Pen ze skrajnie prawicowego Frontu Narodowego powiedziała w sobotę w Koblencji podczas spotkania liderów partii prawicowo-populistycznych, że w Europie powstaje "nowy świat" opierający się na państwach narodowych. "Jesteśmy światkami upadku starego świata i narodzin nowego. Obserwujemy powrót państw narodowych, które chciała zniszczyć globalizacja" - mówiła Le Pen', 3, '2017-01-21 01:00:00+01', NULL, 'https://www.pb.pl/le-pen-proklamuje-powstanie-nowego-swiata-852752');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (22, 'Trump podpisał dekret ws. Obamacare', 'Prezydent USA Donald Trump rozpoczął w piątek urzędowanie od podpisania dekretu, który zobowiązuje agencje federalne do "ograniczenia obciążeń" związanych z reformą ubezpieczeń zdrowotnych, wprowadzoną przez poprzedniego prezydenta Baracka Obamę.', 3, '2017-01-21 01:00:00+01', NULL, 'https://www.pb.pl/trump-podpisal-dekret-ws-obamacare-852747');
