INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (23, 'MF nie wycofuje się projektu centralnego rejestru faktur', 'MF nie wycofuje się z projektu utworzenia centralnego rejestru faktur, jednocześnie będzie analizować wprowadzenie w VAT mechanizmu tzw. split payment (podzielonej płatności) – mówi wicepremier Mateusz Morawiecki. Oba rozwiązania mają pomóc w walce z wyłudzeniami VAT.', 1, '2017-01-21 01:00:00+01', NULL, 'https://www.pb.pl/mf-nie-wycofuje-sie-projektu-centralnego-rejestru-faktur-852750');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (25, 'Coraz więcej NATO w Polsce', 'Ze składek członków NATO budowane są wielkie składy paliwowe, modernizowane lotniska wojskowe i systemy łączności. Znaczne środki zostaną przeznaczone na rozbudowę komórek NATO usytuowanych w Bydgoszczy. Chodzi o budowę obiektów wyposażonych w specjalistyczny sprzęt dla batalionu łączności NATO, Centrum Szkolenia Połączonych Sił NATO oraz grupy Integracyjnej NATO (NFIU) w Polsce.', 1, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Sluzby-mundurowe/301259841-Coraz-wiecej-NATO-w-Polsce.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (26, 'Warszawa: Reprywatyzacja z korupcją w tle', 'Prokuratura bada, czy za decyzjami urzędników w sprawie cennego gruntu w stolicy nie kryła się korupcja. Właśnie ruszyło śledztwo. Chodzi o decyzje korzystne dla bezprawnie reaktywowanej spółki Nowe Dzielnice, dzięki którym mogła ona przejąć wart fortunę teren w Warszawie na Kamionku, do którego nie miała żadnych praw. Zablokowała to prokuratura.', 1, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Reprywatyzacja-w-Warszawie/301259846-Warszawa-Reprywatyzacja-z-korupcja-w-tle.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (27, 'PiS zakaże trzymania psów na łańcuchach', 'Zakaz trzymania psów na łańcuchach, hodowli zwierząt futerkowych, wykorzystywania czworonogów w cyrkach, a także podwyższenie kar za znęcanie się nad zwierzętami – to część elementów ustawy, którą przygotował Parlamentarny Zespół Przyjaciół Zwierząt. To sejmowe gremium liczy 21 posłów i jest zdominowane przez polityków PO. Nieoczekiwanie zyskali ważnego sojusznika – prezesa PiS Jarosława Kaczyńskiego.', 1, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Prawo-i-Sprawiedliwosc/301259848-PiS-zakaze-trzymania-psow-na-lancuchach.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (28, 'Rząd Trumpa szykuje dekrety ws. ograniczenia roli USA w ONZ', 'Administracja prezydenta Donalda Trumpa przygotowuje dekrety, które mają umożliwić drastyczne ograniczenie wydatków USA na ONZ i inne organizacje międzynarodowe, co może unieważnić pewne wielostronne traktaty. Specjalna komisja ma zostać ustanowiona po to, by rekomendować poszczególne cięcia dotacji dla podmiotów międzynarodowych. ', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Prezydent--USA/170129311-Rzad-Trumpa-szykuje-dekrety-ws-ograniczenia-roli-USA-w-ONZ.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (29, 'Ochotnicy tropią uchodźców w Bułgarii i na Węgrzech', 'W Bułgarii, podobnie jak na Węgrzech, coraz większą popularnością cieszą się paramilitarne oddziały ochotników, tropiących przekraczających zieloną granicę migrantów. Leśne patrole stały się popularne przede wszystkim w środowiskach nacjonalistycznych, na terenach przygranicznych z Turcją. Organizacje walczące o prawa człowieka zwracają uwagę na fakt, że sprzeciw wobec działalności tego typu grup jest w Bułgarii w zasadzie znikomy.', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Uchodzcy/170129316-Ochotnicy-tropia-uchodzcow-w-Bulgarii-i-na-Wegrzech.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (30, 'Szwedzka armia obiektem cyberataku', 'Rzecznik wojska Philip Simon potwierdził, że do ataku na system Caxcis doszło i że jest on od tamtej pory wyłączony. Simon odmówił jednak wypowiedzi o innych podjętych w związku z atakiem krokach.', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Polityka/170129320-Szwedzka-armia-obiektem-cyberataku.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (31, 'Niemcy: Wicekanclerz krytykuje Merkel za politykę migracyjną', 'Wicekanclerz Niemiec Sigmar Gabriel (SPD) ostro skrytykował kanclerz Angelę Merkel (CDU), zarzucając jej naiwność i pychę w podejściu do kryzysu migracyjnego.', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Uchodzcy/170129321-Niemcy-Wicekanclerz-krytykuje-Merkel-za-polityke-migracyjna.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (32, '1984" Orwella hitem. Przez rzeczników Trumpa', 'Firma Signet Classics, wydawca „1984” George''a Orwella, podała, że zleciła dodruk książki, która stała się ostatnio bestsellerem na Amazon.com w związku z analogiami między językiem wykorzystanym w powieści a wypowiedziami rzeczników Białego Domu. Sprzedaż gwałtownie przyspieszyła, gdy rzecznik Trumpa Sean Spicer podważył relacje mediów na temat tłumów zebranych na inauguracji prezydenta, a następnie doradczyni Trumpa Kellyanne Conway ogłosiła, że administracja dostarczy w tej sprawie \"fakty alternatywne\". pierwszą osobą, która publicznie porównała taki język do języka stworzonego w powieści Orwella, była znana dziennikarka \"Washington Post\" Karen Tumulty. ', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Prezydent--USA/170129325-1984-Orwella-hitem-Przez-rzecznikow-Trumpa.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (33, 'Finlandia: Więcej zgonów niż urodzeń po raz pierwszy od 1940 roku', 'Liczba urodzeń w Finlandii spada regularnie od sześciu lat. Populacja Finlandii wynosi obecnie 5 502 593 osób - o 15 285 osób więcej niż w 2015 roku. To efekt napływu migrantów, których w 2016 roku do Finlandii przyjechało 32 376. W tym samym czasie z Finlandii wyemigrowało 16 007 osób.', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Spoleczenstwo/170129328-Finlandia-Wiecej-zgonow-niz-urodzen-po-raz-pierwszy-od-1940-roku.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (34, 'Donald Trump zapowiada szybkie rozpoczęcie budowy muru między USA a Meksykiem', 'Dodał, że Meksyk "kompletnie pokryje koszty" budowy. Kilka godzin później pojawiła się informacja, że Trump podpisał dekret ws. budowy muru. Wezwał też do zatrudnienia dodatkowych 5 tys. funkcjonariuszy straży granicznej. Trump wyjaśnił, że na razie za budowę muszą zapłacić amerykańscy podatnicy, ale w ostatecznym rozrachunku Meksyk zrefunduje te koszty.', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Prezydent--USA/170129329-Donald-Trump-zapowiada-szybkie-rozpoczecie-budowy-muru-miedzy-USA-a-Meksykiem.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (35, 'Ukraina o zniszczeniu cmentarza w Bykowni: Prowokacja', 'Szef MSZ Ukrainy Pawło Klimkin potępił w środę zniszczenie cmentarza ofiar NKWD w Bykowni i profanację tamtejszego polskiego cmentarza wojennego. Szef ukraińskiego Instytutu Pamięci Narodowej nazwał incydent prowokacją, mającą skłócić Polaków i Ukraińców.', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Historia/170129334-Ukraina-o-zniszczeniu-cmentarza-w-Bykowni-Prowokacja.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (36, 'Były dowódca wojsk NATO: Ameryka odbuduje armię', 'Po dwóch dekadach zrozumieliśmy, że Kreml nie będzie naszym partnerem. Prezydent Trump chce dostosować do tego Siły Zbrojne USA – zapowiada Jędrzejowi Bieleckiemu były dowódca wojsk NATO w Europie Philip Breedlove.', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Publicystyka/301259849-Byly-dowodca-wojsk-NATO-Ameryka-odbuduje-armie.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (37, 'Stoltenberg: Sojusz na złe czasy', 'Stanęliśmy w obliczu największych w ostatnich dziesięcioleciach wyzwań. Terroryzm, cyberataki i rosnąca zuchwałość Rosji są wzajemnie powiązane – mówi sekretarz generalny NATO Jens Stoltenberg.', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Rozmowy-czwartkowe/301259851-Stoltenberg-Sojusz-na-zle-czasy.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (38, 'Martin Schulz rzuca wyzwanie Angeli Merkel', 'Martin Schulz, były szef Parlamentu Europejskiego, będzie nie tylko kandydatem socjaldemokratów na kanclerza we wrześniowych wyborach parlamentarnych i tym samym konkurentem Angeli Merkel. Schulz objąć ma kierownictwo SPD oraz że nie zostanie członkiem gabinetu Merkel, przynajmniej do wyborów.', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Polityka/301259853-Martin-Schulz-rzuca-wyzwanie-Angeli-Merkel.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (39, 'Moskwa stawia na żołnierzy i czołgi', 'Rosyjska armia rozpoczęła przygotowania do manewrów, może najgroźniejszych od czasów zimnej wojny. Rosyjsko-białoruskie ćwiczenia "Zachód 2017" mają się odbyć jesienią, ale przygotowania już trwają.', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Polityka/301259855-Moskwa-stawia-na-zolnierzy-i-czolgi.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (40, 'Włochy bliżej przedterminowych wyborów', 'Orzeczenie Trybunału otwiera faktycznie drzwi do przeprowadzenia przyspieszonych wyborów parlamentarnych, które są jedną z rozważanych hipotez od czasu dymisji Renziego w rezultacie porażki jego gabinetu w grudniowym referendum konstytucyjnym.', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Unia-Europejska/170129335-Wlochy-blizej-przedterminowych-wyborow.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (41, 'USA-Chiny: Wielkie starcie na morzu', 'Pokerowa zagrywka prezydenta USA z Chinami. Chce powstrzymać ekspansję Pekinu i ustalić nowe zasady handlu. Będziemy musieli wysłać Chinom jasny sygnał, że, po pierwsze, budowa sztucznych wysp musi zostać wstrzymana, a po wtóre, że dostęp do tych wysp nie będzie możliwy.', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Dyplomacja/301259865-USA-Chiny-Wielkie-starcie-na-morzu.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (42, 'Senator Chuck Schumer wyrasta na prawdziwego przywódcę partii', 'Senator Chuck Schumer, lider demokratycznej mniejszości w Senacie USA staje się niebezpiecznym oponentem prezydenta Donalda Trumpa. Choć sam jest uważany za polityka centrowego, sprzymierzył się po wyborach prezydenckich z przedstawicielami lewego skrzydła Demokratów. Są wśród nich senator Bernie Sanders, który ubiegał się o nominację swej partii w wyborach prezydenckich, i wpływowa senator Elizabeth Warren. Lider Demokratów w Senacie stara się też blokować najbardziej kontrowersyjnych kandydatów Trumpa na ważne stanowiska w administracji. Udało mu się odsunąć sporo przesłuchań w Senacie, który musi zatwierdzić nominacje prezydenta', 3, '2017-01-25 01:00:00+01', NULL, 'http://www.rp.pl/Prezydent--USA/170129342-The-Hill-Lider-Demokratow-w-Senacie-staje-sie-problemem-Trumpa.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (43, 'Rząd chce zmienić zasady składania PIT. Podatnika rozliczy urząd skarbowy', 'Zgodnie z uzasadnieniem, jakie napisano w wykazie prac legislacyjnych, zmiany mają polegać na ograniczeniu kosztów związanych z poborem podatku dochodowego od osób fizycznych poprzez zmniejszenie: kosztów związanych z zaopatrzeniem urzędów skarbowych w papierowe wersje druków zeznań; kosztów związanych z obsługą deklaracji składanych papierowo; uwolnienie pracodawców od kosztownego utrzymywania dokumentacji papierowej. Wniosek o sporządzenie zeznania składany byłby wyłącznie elektronicznie za pośrednictwem portalu podatkowego lub banków.', 1, '2017-01-26 01:00:00+01', NULL, 'http://gospodarka.dziennik.pl/podatki/artykuly/541143,pit-37-kiedy-zlozyc-pit-jak-zlozyc-pit.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (44, 'Będzie kontrola w Auchanie ws. 500 plus', 'Główny Inspektor Pracy przeprowadzi kontrolę w sieci marketów Auchan. Pracownicy sieci Auchan otrzymali oni do wypełnienia deklaracje o tym, czy pobierają świadczenie 500 plus i na ile dzieci. Pod groźbą zwolnienia z pracy.', 1, '2017-01-26 01:00:00+01', NULL, 'http://gospodarka.dziennik.pl/news/artykuly/541133,po-artykule-w-dgp-minister-interweniuje-bedzie-kontrola-w-auchanie-ws-500-plus.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (45, 'Wychodzimy z dołka wzrostu PKB', 'Było źle z inwestycjami publicznymi w Polsce, ale relatywnie lepiej niż w innych krajach regionu. IV kw. 2016 r. to prawdopodobnie będzie dołek cyklu. Kolejne kwartały przyniosą już przyspieszenie wzrostu – mówił Jakub Borowski, główny ekonomista Credit Agricole w Polsce', 1, '2017-01-27 01:00:00+01', NULL, 'http://www.rp.pl/Dane-gospodarcze/170129048-RZECZoBIZNESIE-Jakub-Borowski-Wychodzimy-z-dolka-wzrostu-PKB.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (46, 'Polska i Ukraina podzieliły pozwolenia na tiry', 'Kraje podpisały umowę o podziale rocznej kwoty 200 tysięcy pozwoleń na międzynarodowy ruch samochodów ciężarowych.', 1, '2017-01-27 01:00:00+01', NULL, 'http://www.rp.pl/Transport-drogowy/301279975-Polska-i-Ukraina-podzielily-pozwolenia-na-tiry.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (47, 'Gazprom wprowadził zakaz zakupu zagranicznych maszyn i urządzeń.', 'Gazprom poinformował o wprowadzonych zmianach w regulamin zakupów. W przetargach i aukcjach Rosjanie będą faworyzować rosyjskie towary, roboty i usługi. Zasady obowiązują od 1 stycznia 2017 r, ale dopiero teraz koncern o nich informuje opinię publiczną. Priorytetowa pozycja nie dotyczy zakupów urządzeń i technologii u jedynego ich producenta. Koncern wprowadził też zakaz zakupów zagranicznych urządzeń i technologii oprócz tych, których odpowiednika nie ma w Rosji.', 3, '2017-01-27 01:00:00+01', NULL, 'http://www.rp.pl/Energianews/301279976-Gazprom-faworyzuje-swoich.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (48, 'Beata Szydło: w 2018 roku zacznie się rozwijać nowoczesne górnictwo', 'Umówiliśmy się z panem ministrem Tchórzewskim, że do końca tego roku te podstawowe reformy zostaną zakończone i rok 2018 to już będzie rok, w którym zacznie się rozwijać nowoczesne polskie górnictwo. Ma być oparte na nowoczesnych technologiach. Również mamy ambitne plany dotyczące zgazowania węgla.', 1, '2017-01-26 01:00:00+01', NULL, 'http://www.rp.pl/Wegiel/170129206-Beata-Szydlo-w-2018-roku-zacznie-sie-rozwijac-nowoczesne-gornictwo.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (49, 'PGE musi zwrócić 8,1 mln zł', 'Sąd Najwyższy wydał wyroki w sprawie korekty kosztów osieroconych za 2008 r. i 2009 r. dla PGE Górnictwo i Energetyka Konwencjonalna (PGE GiEK) z grupy kapitałowej Polskiej Grupy Energetycznej (PGE), na podstawie których spółka zwróci do Zarządcy Rozliczeń łącznie 8,1 mln zł, podała PGE.', 1, '2017-01-27 01:00:00+01', NULL, 'http://biznesalert.pl/sad-najwyzszy-pge-musi-zwrocic-81-mln-zl/');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (50, '2016 rokiem gazu w europejskiej energetyce', 'Gaz wygrywa, węgiel jest największym przegranym wśród źródeł energii elektrycznej w UE - pierwsze podsumowanie ubiegłego roku w unijnym sektorze wytwarzania. O wypychaniu jednego surowca przez drugi decydują relacje cen.  Raport think tanku Agora Energiewende: produkcja prądu z węgla – w tym brunatnego – w Unii Europejskiej spadła w 2016 r. o niemal 12%, czyli o 94 TWh. Taka ilość energii odpowiada mniej więcej dwóm trzecim jej rocznego zużycia w Polsce.', 1, '2017-01-27 01:00:00+01', NULL, 'http://biznesalert.pl/wn-2016-rokiem-gazu-europejskiej-energetyce/');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (51, 'Shell zamierza sprzedać aktywa na Morzu Północnym', 'Nabywcą naftowo-gazowych aktywów ma być firma Chrysaor wspierana przez fundusz EIG Partners. Wartość transakcji szacowana jest na 3 mld dolarów. Nabywca planuje przejąć mix udziałów w starszych polach naftowych, nowych projektach i infrastrukturze. ', 3, '2017-01-27 01:00:00+01', NULL, 'http://biznesalert.pl/shell-zamierza-sprzedac-aktywa-morzu-polnocnym/');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (52, 'Sejm przyjął nowelę kodeksu karnego o VAT', 'Karę 25 lat więzienia za wyłudzenia VAT na podstawie fikcyjnych faktur o wartości powyżej 10 mln zł przewiduje ustawa o zmianie ustawy Kodeksu karnego i niektórych innych ustaw, którą przyjął Sejm.', 1, '2017-01-26 01:00:00+01', NULL, 'http://gospodarka.dziennik.pl/podatki/artykuly/541165,vat-sejm-wiezienie-kodeks-karny.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (53, 'Amerykański wymiar sprawiedliwości idzie na wojnę z Trumpem. Sądy blokują deportacje', 'Ministerstwo bezpieczeństwa narodowego USA poinformowało w wydanym w sobotę wieczorem oświadczeniu, że będzie stosowało się do decyzji sądów, które zablokowały deportacje kilku osób z Nowego Jorku. Resort podkreślił jednak, że w mocy pozostaje prezydencki dekret o zakazie wjazdu dla obywateli kilku krajów. Oświadczenie wydano kilka godzin po tym, gdy sąd federalny w Nowym Jorku wydał zezwolenie na warunkowy pobyt w USA dwóm osobom, którym odmówiono prawa wjazdu na podstawie dekretu prezydenta Donalda Trumpa, zakazującego przyjmowania obywateli z siedmiu krajów z muzułmańską większością.', 3, '2017-01-29 01:00:00+01', NULL, 'http://wiadomosci.dziennik.pl/swiat/artykuly/541309,usa-resort-bedziemy-respektowac-decyzje-sadow-ale-dekret-obowiazuje.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (54, 'Trump rozmawiał z Merkel i Putinem. ', 'Prezydent USA Donald Trump w sobotę rozmawiał przez telefon z niemiecką kanclerz Angelą Merkel; wymiana poglądów obojga polityków trwała ok. 45 minut - poinformował rzecznik Trumpa Sean Spicer. Następnie Trump rozmawiał z prezydentem Rosji Władimirem Putinem. Rzecznik Białego Domu nie podał żadnych informacji na temat treści rozmów.', 3, '2017-01-28 01:00:00+01', NULL, 'http://wiadomosci.dziennik.pl/swiat/artykuly/541285,usa-trump-rozmawial-z-merkel-i-putinem-szczegolow-nie-podano.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (55, 'Szefowie MSZ Francji i Niemiec przeciwni zniesieniu sankcji wobec Rosji', 'W Paryżu, podczas wspólnej konferencji prasowej z nowym szefem MSZ Niemiec Sigmarem Gabrielem, szef francuskiej dyplomacji Jean-Marc Ayrault powiedział w sobotę, że ewentualne zniesienie sankcji nałożonych na Rosję musi być powiązane z realizacją porozumień z Mińska.', 3, '2017-01-28 01:00:00+01', NULL, 'http://wiadomosci.dziennik.pl/swiat/artykuly/541275,szefowie-msz-francji-i-niemiec-przeciwni-zniesieniu-sankcji-wobec-rosji.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (56, 'Kijowski wybrany na szefa mazowieckiego KOD. Jeden z kandydatów zrezygnował', 'Walne zebranie członków mazowieckiego KOD wybrało w sobotę po południu lidera Komitetu Mateusza Kijowskiego na przewodniczącego zarządu regionu. Kijowski zdobył w głosowaniu 487 głosów. Jego konkurent Paweł Bilski otrzymał zaś 235 głosów. Z kandydowania zrezygnował ostatecznie działający dotąd m.in. w sekcji programowej mazowieckiego KOD Marcin Skubiszewski, który przed głosowaniem powiedział, że podobieństwa pomiędzy jego kandydaturą a kandydaturą Pawła Bilskiego "znacznie przekraczają różnice".', 1, '2017-01-28 01:00:00+01', NULL, 'http://wiadomosci.dziennik.pl/polityka/artykuly/541281,mateusz-kijowski-wybrany-na-szefa-mazowieckiego-kod.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (57, 'ONZ i IOM wzywają USA, by nie przestawały przyjmować uchodźców', 'Po podpisaniu przez prezydenta Donalda Trumpa dekretu zawieszającego przyjmowanie przez USA uchodźców ONZ oraz Międzynarodowa Organizacja do Spraw Migracji (IOM) zaapelowały w sobotę do Stanów Zjednoczonych, by nie odchodziły od tradycji przyjmowania uchodźców. Obie agencje, które mają siedzibę w Genewie, podkreślają w oświadczeniu, że program oferujący szanse na przesiedlenie osobom zagrożonym w ich własnych krajach ma znaczenie fundamentalne.Dekret podpisany w piątek przez Trumpa zawiesza do odwołania przyjmowanie uchodźców z Syrii, a uchodźców z innych krajów - na 120 dni. Rozporządzenie przewiduje także wstrzymanie przez 90 dni wydawania amerykańskich wiz obywatelom krajów muzułmańskich mających problemy z terroryzmem.', 3, '2017-01-28 01:00:00+01', NULL, 'http://wiadomosci.dziennik.pl/swiat/artykuly/541276,onz-i-iom-wzywaja-usa-by-nie-przestawaly-przyjmowac-uchodzcow.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (58, 'Kaczyński deklaruje: Nie będzie przyspieszonych wyborów samorządowych', '"Nie będzie przyspieszonych wyborów samorządowych, odbędą się one na jesieni przyszłego roku" – powiedział prezes PiS Jarosław Kaczyński w wywiadzie dla Radia Kraków. Zapowiedział, że jego ugrupowanie będzie dążyć do wprowadzenia w nich zasady kadencyjności, ale zdecyduje o tym TK.', 1, '2017-01-28 01:00:00+01', NULL, 'http://wiadomosci.dziennik.pl/polityka/artykuly/541278,kaczynski-nie-bedzie-przyspieszonych-wyborow-samorzadowych.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (59, 'Poseł CDU: Berlin musi włączyć Polskę do osi z Paryżem', 'Członek komisji spraw zagranicznych Bundestagu Karl-Georg Wellmann (CDU) powiedział, że UE musi sama zatroszczyć się o bezpieczeństwo, zamiast "oglądać się na ojca chrzestnego w Waszyngtonie", a Berlin musi włączyć do osi niemiecko-francuskiej Polskę.', 3, '2017-01-28 01:00:00+01', NULL, 'http://wiadomosci.dziennik.pl/swiat/artykuly/541268,niemcy-posel-cdu-polska.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (60, 'To koniec Misiewicza w MON? Premier: Rozmawiałam o nim z szefem resortu obrony', 'Beata Szydło nie chciała jasno powiedzieć, czy ostatnie informacje o zachowaniu Bartłomieja Misiewicza przeważyły szale i rzecznik MON będzie musiał odejść. Stwierdziła tylko, że rozmawiała o tej sprawie z Antonim Macierewiczem. Chodzi o doniesienia "Faktu", jakoby Bartłomiej Misiewicz "szalał" w jednym z białostockich lokali - stawiał alkohol, proponował ludziom prace. Rzecznik MON stanowczo zaprzeczył tym informacjom.', 1, '2017-01-28 01:00:00+01', NULL, 'http://wiadomosci.dziennik.pl/polityka/artykuly/541264,bartlomiej-misiewicz-beata-szydlo-mon-antoni-macierewicz.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (61, 'Generał Andrzej Pawlikowski nie jest już szefem BOR', 'Gen. bryg. Andrzej Pawlikowski został odwołany ze stanowiska szefa Biura Ochrony Rządu; jego obowiązki pełni zastępca szefa BOR płk Tomasz Kędzierski - poinformowała premier Beata Szydło. Premier powiedziała, że gen. Andrzej Pawlikowski poprosił o zwolnienie z służby i przechodzi na emeryturę ze względu na stan zdrowia. ', 1, '2017-01-28 01:00:00+01', NULL, 'http://wiadomosci.dziennik.pl/polityka/artykuly/541257,szef-bor-premier-beata-szydlo-gen-andrzej-pawlikowski.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (62, 'Trump zamyka granice dla wielu muzułmanów.', 'Prezydent Donald Trump podpisał w piątek dekret, który zawiesza przyjmowanie przez USA uchodźców, w tym z Syrii. Według tego rozporządzenia wstrzymane zostaje także wydawanie wiz obywatelom siedmiu krajów, których większość mieszkańców stanowią muzułmanie.', 3, '2017-01-28 01:00:00+01', NULL, 'http://wiadomosci.dziennik.pl/swiat/artykuly/541254,donald-trump-muzulmanie-zatrzymanie-imigracji.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (63, 'Różnica zdań między May a Trumpem. Chodzi o sankcje wobec Rosji', 'Theresa May i prezydent USA Donald Trump podczas piątkowej wizyty premier Wielkiej Brytanii w Waszyngtonie rozmawiali o specjalnej relacji łączącej ich kraje, o wolnym handlu oraz woli wspierania NATO. Poruszyli też kwestię sankcji wobec Rosji.', 3, '2017-01-27 01:00:00+01', NULL, 'http://rss.dziennik.pl/~r/Dziennik-Wiadomosci/~3/Lmbun9EKWjw/541248,roznica-zdan-miedzy-may-a-trumpem-chodzi-o-sankcje-wobec-rosji.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (64, 'CBA w Mielcu. Chodzi o przetarg na śmigłowce', 'Funkcjonariusze Delegatury Centralnego Biura Antykorupcyjnego w Warszawie w ramach śledztwa prowadzonego pod nadzorem Prokuratury Regionalnej w Szczecinie w sprawie rozpisanego w 2012 r. przetargu na śmigłowce wielozadaniowe dla Wojsk Lądowych, Marynarki Wojennej i Sił Powietrznych realizują dziś postanowienia o żądaniu wydania rzeczy, zabezpieczają dokumenty w Ministerstwie Rozwoju w Warszawie, w Polskich Zakładach Lotniczych sp. z o.o. w Mielcu oraz w Wytwórni Sprzętu Komunikacyjnego „PZL-Świdnik” S.A. w Świdniku - przekazuje Piotr Kaczorek z wydziału komunikacji społecznej CBA.', 1, '2017-01-24 01:00:00+01', NULL, 'http://www.polskatimes.pl/fakty/kraj/a/cba-w-mielcu-chodzi-o-przetarg-na-smiglowce,11723752/');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (65, 'Od jesieni zakaz palenia mułem.', 'Małopolskie jest pierwszym województwem, w którym przyjęto uchwałę antysmogową dla całego regionu. Przyjęto ją w poniedziałek, a ma obowiązywać od lipca. Woj. śląskie będzie drugim. Projekt regulacji dla regionu już jest. Na razie Urząd Marszałkowski czeka na opinie. Konsultacje potrwają do 6 lutego. I są ważne, bo nowe przepisy zakładają, że w piecach nie będzie można palić mułem i flotem (a kopalnie sporo na ich sprzedaży zarabiają), czyli paliwem najtańszym.', 1, '2017-01-25 01:00:00+01', NULL, 'http://www.polskatimes.pl/fakty/kraj/a/smog-nas-dusi-ale-juz-ostatni-rok-od-jesieni-zakaz-palenia-mulem,11725454/');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (66, 'Merkel krytykuje dekret Trumpa.', 'Kanclerz Niemiec Angela Merkel uważa za niesłuszny dekret podpisany przez prezydenta USA Donalda Trumpa ws. zakazu wjazdu dla uchodźców i obywateli kilku państw z muzułmańską większością - oświadczył w niedzielę rzecznik niemieckiego rządu Steffen Seibert.', 3, '2017-01-29 01:00:00+01', NULL, 'http://www.rp.pl/Prezydent--USA/170128899-Kanclerz-Merkel-uwaza-dekret-Trumpa-za-niesluszny.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (67, 'Po dekrecie Trumpa: Trudeau zaprasza uchodźców do Kanady', 'Dzień po zakazie wjazdu obywateli siedmiu krajów do USA premier Kanady Justin Trudeau zaprosił do swojej ojczyzny uchodźców. - Przyjmiemy was z otwartymi rękami - zapewnia.', 3, '2017-01-29 01:00:00+01', NULL, 'http://www.rp.pl/Swiat/170128905-Po-dekrecie-Trumpa-Trudeau-zaprasza-uchodzcow-do-Kanady.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (68, 'Hollande ostrzega Trumpa przed protekcjonizmem.', 'Prezydent Francji w rozmowie telefonicznej, którą odbył w sobotę z prezydentem USA, podkreślił, że będzie dążył do konsolidacji Unii Europejskiej, a sojusz wojskowy w ramach NATO uważa za nieodzowny i niemożliwy do zastąpienia. Przestrzegł przed protekcjonizmem. Francois Hollande podkreślił w rozmowie z Donaldem Trumpem konieczność umacniania się Stanów Zjednoczonych we wszystkich dziedzinach, włącznie z obroną, a jednocześnie nieodzowność utrzymania Sojuszu Północnoatlantyckiego. Hollande ostrzegł Trumpa przed "izolacjonizmem" i apelował o poszanowanie zasady "przyjmowania uchodźców".', 3, '2017-01-28 01:00:00+01', NULL, 'http://www.rp.pl/Prezydent--USA/170128915-Rozmowa-telefoniczna-prezydentow-Francois-Hollandea-i-Donalda-Trumpa.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (69, 'Tajemnicza śmierć byłego szefa KGB', 'Były szef KGB i FSB Oleg Erowinkin, który zdaniem ekspertów mógł być źródłem informacji do raportu o rosyjskich materiałach kompromitujących prezydenta USA Donalda Trumpa, mógł paść ofiarą politycznego mordu. Jak podała gazeta, ciało Erowinkina zostało znalezione w Moskwie 26 grudnia ub. roku na tylnym siedzeniu jego samochodu. Według części rosyjskich mediów, przyczyną śmierci miał być zawał serca.Brytyjski dziennik podkreśla jednak, że Erowinkin miał bliskie relacje z Igorem Seczinem, byłym wicepremierem Rosji i obecnym prezesem rosyjskiej państwowej firmy paliwowej Rosnieft oraz często funkcjonował jako łącznik pomiędzy nim a prezydentem Władimirem Putinem.', 3, '2017-01-28 01:00:00+01', NULL, 'http://www.rp.pl/Przestepczosc/170128916-Telegraph-Tajemnicza-smierc-bylego-szefa-KGB.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (70, 'Benjamin Netanjahu popiera plany prezydenta USA w sprawie budowy muru', 'Premier Izraela Benjamin Netanjahu poparł w sobotę na swoim Twitterze plan prezydenta USA Donalda Trumpa dotyczący wzniesienia muru wzdłuż granicy z Meksykiem i podkreślił, że zagrodzenie granicy z Egiptem zatrzymało napływ afrykańskich imigrantów do Izraela.', 3, '2017-01-28 01:00:00+01', NULL, 'http://www.rp.pl/Prezydent--USA/170128922-Benjamin-Netanjahu-popiera-plany-prezydenta-USA-w-sprawie-budowy-muru.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (71, 'Iran: Zakaz wjazdu dla obywateli USA w odwecie za dekret Trumpa', 'Iran oświadczył w sobotę, że zakaże obywatelom USA wjazdu do kraju w ramach kroków odwetowych po podpisaniu przez prezydenta Donalda Trumpa dekretu zakazującego przyznawania wiz obywatelom Iranu i sześciu innych zamieszkanych głównie przez muzułmanów krajów.', 3, '2017-01-28 01:00:00+01', NULL, 'http://www.rp.pl/Prezydent--USA/170128923-Iran-Zakaz-wjazdu-dla-obywateli-USA-w-odwecie-za-dekret-Trumpa.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (72, 'Michaił Gorbaczow ostrzega: "Świat przygotowuje się do wojny."', 'Gorbaczow wezwał przywódców, by położyć kres do takim zagrożeniom przez wysuniętą rezolucję ONZ, która zasadniczo zakazuje wojny nuklearnej. Gorbaczow, za zagrożenie wojną, obwinia słabe relacje pomiędzy najbardziej rozwiniętymi krajami.', 3, '2017-01-28 01:00:00+01', NULL, 'http://abcnews.go.com/Politics/gorbachev-urges-trump-putin-introduce-resolution-banning-nuclear/story?id=45084217&cid=clicksource_4380645_2_three_posts_vert_hed');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (73, 'Awdijiwka: Trzech ukraińskich żołnierzy zginęło w walce z separatystami', 'Trzech żołnierzy sił rządowych Ukrainy zginęło w wyniku walk z prorosyjskimi separatystami, do których doszło w niedzielę w strefie przemysłowej miasta Awdijiwka na północ od Doniecka. Według ukraińskiej policji bojownicy ostrzelali dzielnicę mieszkalną Awdijiwki. Ranni zostali dwaj cywile. Separatyści przypuścili atak na pozycje ukraińskich wojsk o godzinie 5 nad ranem czasu lokalnego (4 w Polsce). Najpierw ostrzelali je z artylerii, a potem podjęli próbę wyparcia przeciwnika. Motuzianyk powiedział, że siły rządowe odparły atak, a strona przeciwna "poniosła znaczne straty".', 5, '2017-01-29 01:00:00+01', NULL, 'http://www.rp.pl/Konflikt-na-Ukrainie/170128873-Awdijiwka-Trzech-ukrainskich-zolnierzy-zginelo-w-walce-z-separatystami.html');
INSERT INTO articles (id, title, descr, category_id, on_date, created_date, link) VALUES (74, 'Protesty na lotniskach po ogłoszeniu dekretu Trumpa', 'Podpisanie przez prezydenta USA Donalda Trumpa dekretu zawieszającego przyjmowanie w USA m.in. uchodźców z Syrii i zakazującego wjazdu osobom z siedmiu państw z muzułmańską większością spotkało się z krytyką na świecie i wywołało liczne protesty w samych USA. Prezydencki dekret wywołał zamieszanie na lotniskach na całym świecie, gdzie służby celne i imigracyjne starały się poprawnie zinterpretować decyzję Trumpa. Dekret Trumpa spotkał się też z krytyczną reakcją licznych państw, m.in. premiera Kanady Justina Trudeau, brytyjskiej premier Theresy May, premiera Turcji Binalego Yildirima czy szefa francuskiego MSZ Jean-Marca Ayrault.', 3, '2017-01-29 01:00:00+01', NULL, 'http://www.rp.pl/Prezydent--USA/170128893-Protesty-na-lotniskach-po-ogloszeniu-dekretu-Trumpa.html');
