INSERT INTO rss_draft_entries (link, title, lead_content, pub_date, rssfeed_id, created_date, accepted, rejected, modyfied_date) VALUES ('http://www.polskatimes.pl/fakty/swiat/a/grozny-zatrzymano-mezczyzn-planujacych-zamach-na-przywodce-czeczenii-ramzana-kadyrowa,11744459/', 'Grozny: Zatrzymano mężczyzn planujących zamach na przywódce Czeczenii Ramzana Kadyrowa', 'Rosyjskie służby specjalne zatrzymały trzech mężczyzn podejrzanych o szykowanie zamachu na Ramzana Kadyrowa, promoskiewskiego przywódcę Czeczenii. Zamach miał być dokonany m.in. przy użyciu granatników oraz materiałów wybuchowych.', '2017-01-30 21:01:00+01', 10, '2017-02-01 18:07:42.097+01', false, false, NULL);
