INSERT INTO rss_draft_entries (link, title, lead_content, pub_date, rssfeed_id, created_date, accepted, rejected, modyfied_date) VALUES ('http://www.polskatimes.pl/fakty/swiat/a/usa-protesty-przeciw-polityce-imigracyjnej-trumpa-tu-nie-chodzi-o-religie-video,11741621/', 'USA: Protesty przeciw polityce imigracyjnej Trumpa. "Tu nie chodzi o religię" [VIDEO]', 'Nowy Jork, Waszyngton, Houston, Boston – największe amerykańskie miasta protestują przeciwko zaostrzeniu polityki imigracyjnej przez Donalda Trumpa. Sam polityk powtarza: „dekret nie jest wymierzony w muzułmanów, chodzi o zapewnienie nam bezpieczeństwa”.', '2017-01-30 09:50:00+01', 10, '2017-02-01 18:07:42.101+01', false, false, NULL);
