INSERT INTO rss_draft_entries (link, title, lead_content, pub_date, rssfeed_id, created_date, accepted, rejected, modyfied_date) VALUES ('http://www.polskatimes.pl/fakty/swiat/a/niemcy-chca-objac-elektronicznym-nadzorem-potencjalnych-terrorystow,11735760/', 'Niemcy chcą objąć elektronicznym nadzorem potencjalnych terrorystów', 'Zdaniem niemieckich służb na terenie kraju może być 600 ludzi podejrzewanych o terroryzm. To ich powinno się w pierwszym rzędzie objąć elektronicznym nadzorem. Do tego jednak trzeba zmienić obecnie obowiązujące w Niemczech prawo.', '2017-01-27 20:00:00+01', 10, '2017-02-01 18:07:42.107+01', false, false, NULL);
